INSERT INTO `new_list` VALUES (61, '3', '公告', '【ข้อมูล】《มังกรหยก2 M》พบกับเคล็ดวิชาเลื่องชื่อ', 'หลายคนเมื่ออ่านนวนิยายศิลปะการต่อสู้ คงนึกถึงกระบวนท่าต่างๆที่มีอยู่มากมาย ซึ่งกระบวนท่าการต่อสู้ต่างๆนั้น ได้ถูกรวบรวมมาให้ท่านได้สัมผัสในเกมส์มังกรหยก2 ในรูปแบบการประลองยุทธแบบ เทิร์นเบนด์ ซึ่งจะทำให้ท่านได้ลิ้มรสวิชาการต่อสู้ต่างๆอย่างแท้จริง!!,วรยุทธ์ต่อสู้ใดที่แข็งแกร่งที่สุด? ในมังกรหยก2 กระบวนท่าทั้ง6ทิศ มีหลายกระบวนท่าที่ทุกท่านรู้จักกันดี มังกรหยก2จะนำท่านเข้าสู่โลกของยุทธจักรอันดุเดือดแบบที่ไม่เคยพบที่ใดมาก่อน,วรยุทธ์เฉพาะของลัทธิวัชรยาน', '2020-05-20', 'G:\\game_vue\\tchbackend\\sql\\upload\\42ad42ad9430283916189868117afe88,G:\\game_vue\\tchbackend\\sql\\upload\\3b3149d93cce47873ee6290ce60b4b5b,G:\\game_vue\\tchbackend\\sql\\upload\\e43fdc25a6aea4ccf4cc11f3098c9f1d,G:\\game_vue\\tchbackend\\sql\\upload\\1ecd0c139996eb31acb9e0f5cb4e17ad');

